// Processar assinaturas ativas
const activeSubscriptions = $('Buscar Assinaturas Ativas').all();
const cancelledSubscriptions = $('Buscar Cancelamentos Recentes').all();

// Análise de assinaturas ativas
const activeAnalysis = activeSubscriptions.map(item => {
  const sub = item.json;
  const createdDate = new Date(sub.creation_date);
  const now = new Date();
  const daysActive = Math.floor((now - createdDate) / (1000 * 60 * 60 * 24));

  return {
    json: {
      // Informações básicas
      subscriptionId: sub.subscription_id,
      status: sub.status,
      productName: sub.product?.name || 'N/A',

      // Cliente
      customerEmail: sub.buyer?.email || '',
      customerName: sub.buyer?.name || '',

      // Financeiro
      price: sub.price?.value || 0,
      formattedPrice: `R$ ${(sub.price?.value || 0).toFixed(2).replace('.', ',')}`,
      nextPaymentDate: sub.next_payment_date ? new Date(sub.next_payment_date).toLocaleDateString('pt-BR') : 'N/A',

      // Métricas
      daysActive,
      isNewCustomer: daysActive <= 30,
      isAtRisk: daysActive > 180, // Cliente há mais de 6 meses pode estar em risco

      // Dados originais
      original: sub
    }
  };
});

// Análise de cancelamentos
const cancelAnalysis = cancelledSubscriptions.map(item => {
  const sub = item.json;
  const cancelDate = new Date(sub.cancellation_date || sub.last_status_change);
  const daysSinceCancellation = Math.floor((new Date() - cancelDate) / (1000 * 60 * 60 * 24));

  return {
    json: {
      subscriptionId: sub.subscription_id,
      productName: sub.product?.name || 'N/A',
      customerEmail: sub.buyer?.email || '',
      customerName: sub.buyer?.name || '',
      cancelledAt: cancelDate.toLocaleDateString('pt-BR'),
      daysSinceCancellation,
      lastPrice: sub.price?.value || 0,
      cancellationReason: sub.cancellation_reason || 'Não especificado',
      original: sub
    }
  };
});

// Criar resumo geral
const summary = {
  json: {
    overview: {
      totalActive: activeSubscriptions.length,
      totalCancelled: cancelledSubscriptions.length,
      newCustomers: activeAnalysis.filter(a => a.json.isNewCustomer).length,
      atRiskCustomers: activeAnalysis.filter(a => a.json.isAtRisk).length,

      // Receita mensal recorrente (MRR)
      mrr: activeAnalysis.reduce((sum, a) => sum + a.json.price, 0),
      mrrFormatted: `R$ ${activeAnalysis.reduce((sum, a) => sum + a.json.price, 0).toFixed(2).replace('.', ',')}`,

      // Churn dos últimos 7 dias
      churnRate: ((cancelledSubscriptions.length / (activeSubscriptions.length + cancelledSubscriptions.length)) * 100).toFixed(2) + '%',

      generatedAt: new Date().toLocaleString('pt-BR')
    },
    activeSubscriptions: activeAnalysis.map(a => a.json),
    recentCancellations: cancelAnalysis.map(c => c.json)
  }
};

return [summary];
