// Consolidar dados de vendas e comiss√µes
const sales = $node["Buscar Vendas da Semana"].all();
const commissions = $node[

].all();

// An√°lise de vendas
const salesAnalysis = sales.map(item => {
  const sale = item.json;
  return {
    transactionId: sale.transaction,
    productName: sale.product?.name || 'N/A',
    productId: sale.product?.id,
    buyerEmail: sale.buyer?.email,
    buyerName: sale.buyer?.name,
    saleDate: new Date(sale.purchase_date),
    value: sale.price?.value || 0,
    currency: sale.price?.currency || 'BRL',
    paymentMethod: sale.payment?.method,
    status: sale.status,
    hasAffiliate: !!sale.affiliate?.name
  };
});

// An√°lise de comiss√µes
const commissionsMap = new Map();
commissions.forEach(item => {
  const comm = item.json;
  commissionsMap.set(comm.transaction, {
    affiliateName: comm.affiliate?.name,
    commissionValue: comm.commission?.value || 0,
    commissionStatus: comm.status
  });
});

// M√©tricas por produto
const productMetrics = {};
salesAnalysis.forEach(sale => {
  const key = sale.productId || 'unknown';
  if (!productMetrics[key]) {
    productMetrics[key] = {
      productName: sale.productName,
      totalSales: 0,
      revenue: 0,
      buyers: new Set()
    };
  }
  productMetrics[key].totalSales++;
  productMetrics[key].revenue += sale.value;
  productMetrics[key].buyers.add(sale.buyerEmail);
});

// Converter para array e calcular m√©tricas finais
const productsSummary = Object.values(productMetrics).map(p => ({
  productName: p.productName,
  totalSales: p.totalSales,
  revenue: p.revenue,
  uniqueBuyers: p.buyers.size,
  averageTicket: (p.revenue / p.totalSales).toFixed(2)
})).sort((a, b) => b.revenue - a.revenue);

// M√©tricas gerais da semana
const weekMetrics = {
  period: {
    start: $now.minus(7, 'days').startOf('day').toFormat('dd/MM/yyyy'),
    end: $now.minus(1, 'days').endOf('day').toFormat('dd/MM/yyyy')
  },
  totalSales: salesAnalysis.length,
  totalRevenue: salesAnalysis.reduce((sum, s) => sum + s.value, 0),
  totalCommissions: Array.from(commissionsMap.values()).reduce((sum, c) => sum + c.commissionValue, 0),
  uniqueBuyers: new Set(salesAnalysis.map(s => s.buyerEmail)).size,
  averageTicket: (salesAnalysis.reduce((sum, s) => sum + s.value, 0) / salesAnalysis.length).toFixed(2),

  // Top produtos
  topProducts: productsSummary.slice(0, 5),

  // Vendas por dia
  salesByDay: {},

  // M√©todos de pagamento
  paymentMethods: {}
};

// An√°lise por dia da semana
const daysOfWeek = ['Domingo', 'Segunda', 'Ter√ßa', 'Quarta', 'Quinta', 'Sexta', 'S√°bado'];
salesAnalysis.forEach(sale => {
  const dayKey = sale.saleDate.toISOString().split('T')[0];
  const dayName = daysOfWeek[sale.saleDate.getDay()];

  if (!weekMetrics.salesByDay[dayKey]) {
    weekMetrics.salesByDay[dayKey] = {
      dayName,
      sales: 0,
      revenue: 0
    };
  }
  weekMetrics.salesByDay[dayKey].sales++;
  weekMetrics.salesByDay[dayKey].revenue += sale.value;

  // M√©todos de pagamento
  const method = sale.paymentMethod || 'Outros';
  if (!weekMetrics.paymentMethods[method]) {
    weekMetrics.paymentMethods[method] = {
      count: 0,
      revenue: 0
    };
  }
  weekMetrics.paymentMethods[method].count++;
  weekMetrics.paymentMethods[method].revenue += sale.value;
});

// Formatar valores em Real
weekMetrics.totalRevenueFormatted = `R$ ${weekMetrics.totalRevenue.toFixed(2).replace('.', ',')}`;
weekMetrics.totalCommissionsFormatted = `R$ ${weekMetrics.totalCommissions.toFixed(2).replace('.', ',')}`;
weekMetrics.netRevenueFormatted = `R$ ${(weekMetrics.totalRevenue - weekMetrics.totalCommissions).toFixed(2).replace('.', ',')}`;

return [{
  json: {
    reportType: 'WEEKLY_SALES_REPORT',
    generatedAt: new Date().toLocaleString('pt-BR'),
    metrics: weekMetrics,
    detailedSales: salesAnalysis
  }
}];

